import React, {Component} from 'react'
import MenuItem from '../menu-item/menu-item.component'

import './directory.styles.scss'
//needs to be class to store state values of menu items


class Directory extends Component {
    constructor() {
        super()
        this.state = {
            sections: 
            [{
                    title: 'hats',
                    imageUrl: 'https://i.ibb.co/cvpntL1/hats.png',
                    id: 1,
                    linkUrl: 'shop/hats'
                  },
                  {
                    title: 'jackets',
                    imageUrl: 'https://i.ibb.co/px2tCc3/jackets.png',
                    id: 2,
                    linkUrl: 'shop/jackets'
                  },
                  {
                    title: 'sneakers',
                    imageUrl: 'https://i.ibb.co/0jqHpnp/sneakers.png',
                    id: 3,
                    linkUrl: 'shop/sneakers'
                  },
                  {
                    title: 'womens',
                    imageUrl: 'https://i.ibb.co/GCCdy8t/womens.png',
                    size: 'large',
                    id: 4,
                    linkUrl: 'shop/womens'
                  },
                  {
                    title: 'mens',
                    imageUrl: 'https://i.ibb.co/R70vBrQ/men.png',
                    size: 'large',
                    id: 5,
                    linkUrl: 'shop/mens'
                  }]
        }
    }
    //destructor title, imageUrl, and id off section in map
    //instead of passing each indvidual prop use ...otherSectionProps to represent them
    //you will still need id seperated out to use in the map key
        render () {
            return (
                <div className='directory-menu'>
                    {this.state.sections.map(({id, ...otherSectionProps}) => (
                        <MenuItem key={id} {...otherSectionProps}/>
                    ))}
                </div>
            )
        }
}




export default Directory;
